CREATE TABLE IF NOT EXISTS odontologos (id INT AUTO_INCREMENT PRIMARY KEY, apellido VARCHAR(45) NOT NULL, nombre VARCHAR(45) NOT NULL, matricula VARCHAR(45) NOT NULL UNIQUE);
CREATE TABLE IF NOT EXISTS pacientes (id INT AUTO_INCREMENT PRIMARY KEY, apellido VARCHAR(45) NOT NULL, nombre VARCHAR(45) NOT NULL, domicilio VARCHAR(45), dni VARCHAR(45) NOT NULL UNIQUE, fechaAlta DATE);
CREATE TABLE IF NOT EXISTS turnos (id INT AUTO_INCREMENT PRIMARY KEY, paciente VARCHAR(45) NOT NULL, odontologo VARCHAR(45) NOT NULL, fecha DATE NOT NULL, hora TIME NOT NULL)